definition.script:
pipeline{
    agent{ label 'master' }
    stages{
        stage('Gitlab Checkout '){
            steps{
                script{
                    cleanWs()
                    checkout([$class: 'GitSCM', branches: [[name: '*/master']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'GitLab', url: 'http://3.6.38.166/gitlab/ftdp-admin/Ansible-playbook.git']]])
                }
            }
        }

        stage('Nexus Download') {
            steps{
                script{
                    withCredentials([usernamePassword(credentialsId: 'Nexus', passwordVariable: 'PASS', usernameVariable: 'USER')]){
                        sh 'wget --user $USER --password $PASS http://3.6.38.166/nexus/repository/maven-releases/be/cegeka/gameoflife/0.0.1/gameoflife-0.0.1.war'
                    }
                }
            }
        }

        stage('Ansible Deploy'){
            steps{
                script{
                    sh '''
                    chmod 400 key2.pem
                    ansible-playbook -i host deploy.yml --extra-vars "workspace=$WORKSPACE"
                    '''
                }
            }
        }
    }
}
