definition.script:
pipeline{
    agent { label 'master' }
    stages{
        stage('Svn Checkout'){
            steps{
                script{
                    checkout([$class: 'SubversionSCM', additionalCredentials: [], excludedCommitMessages: '', excludedRegions: '', excludedRevprop: '', excludedUsers: '', filterChangelog: false, ignoreDirPropChanges: false, includedRegions: '', locations: [[cancelProcessOnExternalsFail: true, credentialsId: 'svn', depthOption: 'infinity', ignoreExternalsOption: true, local: '.', remote: 'http://52.66.103.232/svn/gameoflife']], quietOperation: true, workspaceUpdater: [$class: 'UpdateUpdater']])
                }
            }
        }

        stage('Maven Execution'){
            steps{
                script{
                    withMaven(jdk: 'jdk8', maven: 'Maven') {
                        sh 'mvn clean install'   
                    }
                }
            }
        }

        stage('Sonarqube analysis'){
            steps{
                script{
                    def sonar=tool name: 'SonarScanner', type: 'hudson.plugins.sonar.SonarRunnerInstallation'
                    sh ' $sonar -Dsonar.language=java -Dsonar.projectKey=Game -Dsonar.projectName=Gameoflife -Dsonar.projectVersion=0.0.1 -Dsonar.sources=src'
                }
            }
        }

        stage('Nexus Upload'){
            steps{
                script{
                    nexusArtifactUploader artifacts: [[artifactId: 'gameoflife', classifier: '', file: 'target/gameoflife-0.0.1.war', type: 'war']], credentialsId: 'Nexus', groupId: 'be.cegeka', nexusUrl: '52.66.103.232/nexus', nexusVersion: 'nexus3', protocol: 'http', repository: 'maven-releases', version: '0.0.1'
                }
            }
        }
    }
}
